/** The featureList is the list of all the features that an assoc has, if it's only one feature, we don't need to make the combinations if it the product has more than one feature we have to make the combinations in order to follow the shopify format.
NOTE: Shopify supports products that has at most 3 features. **/
variant = [:] variant.name = featureList[0]?.productFeatureDescription variant.option1 = variant.name variant.option2 = featureList[1]?.productFeatureDescription variant.option3 = featureList[2]?.productFeatureDescription variant.price = priceSet.price variant.inventory_management = 'shopify' variant.barcode = prodUPC.idValue variant.weight = productDimension.value variant.weight_unit = uom.abbreviation variant.sku = prodSKU.idValue
if(featureList.size() == 1){ prodVariants.add(new HashMap([name:variant.name, option1:variant.option1, price:variant.price, inventory_management:variant.inventory_management, barcode:variant.barcode, weight:variant.weight, weight_unit:variant.weight_unit, sku:variant.sku])) }else if(featureList.size() == 2){ prodVariants.add(new HashMap([name:variant.name + "/" + variant.option2, option1:variant.option1, option2:variant.option2, price:variant.price, inventory_management:variant.inventory_management, barcode:variant.barcode, weight:variant.weight, weight_unit:variant.weight_unit, sku:variant.sku])) }else if(featureList.size() == 3){ prodVariants.add(new HashMap([name:variant.name + "/" + variant.option2 + "/" + variant.option3, option1:variant.option1, option2:variant.option2, option3:variant.option3, price:variant.price, inventory_management:variant.inventory_management, barcode: variant.barcode, weight:variant.weight, weight_unit:variant.weight_unit, sku:variant.sku])) }

def optionSet = prodOptionsMap.get(item)
if (!optionSet) {
    prodOptionsMap.put(item, [feature.productFeatureDescription])
